select brand as device, sum(a.new_user_count) as newcount,sum(a.launch_user_count) as launchcount from ( select brand,sum(new_user_count)as new_user_count,sum(launch_user_count) as launch_user_count from ( select a.brand,a.new_user_count,a.launch_user_count from ( select * FROM la_device_stat a
a.appid=${appid}
and a.device='${device}'
and a.brand='${brand}'
and a.date >='${date1}'
AND a.date <='${date2}'
) a where not exists(select 1 from tmp_ds b where a.brand=b.brand and a.device=b.device) ) b group by brand union all select * from ( select case when b.fn is not null then b.fn else a.brand end as brand,a.new_user_count,a.launch_user_count from ( select * FROM la_device_stat a
a.appid=${appid}
and a.device='${device}'
and a.brand='${brand}'
and a.date >='${date1}'
AND a.date <='${date2}'
) a inner join tmp_ds b on STRCMP(a.brand,b.brand)=0 and STRCMP(a.device,b.device)=0 ) b )a group by brand order by launchcount desc

