select CONCAT(brand,'/',device) as device,newcount,launchcount from ( select a.brand,a.device, sum(new_user_count) as newcount,sum(launch_user_count) as launchcount FROM ( select brand,device,sum(new_user_count)as new_user_count,sum(launch_user_count) as launch_user_count from ( select a.brand,a.device,a.new_user_count,a.launch_user_count from ( select * FROM la_device_stat a
appid=${appid}
and a.brand='${brand}'
and date >='${date1}'
AND date <='${date2}'
) a where not exists(select 1 from tmp_ds b where a.brand=b.brand and a.device=b.device) ) b group by brand,device union all select fn as brand,device,new_user_count,launch_user_count from ( select a.brand,b.fn,a.device,a.new_user_count,a.launch_user_count from ( select * FROM la_device_stat a
appid=${appid}
and date >='${date1}'
AND date <='${date2}'
) a inner join tmp_ds b on STRCMP(a.brand,b.brand)=0 and STRCMP(a.device,b.device)=0 ) b
brand='${brand}' and fn='${brand}'
brand='${brand}' and fn='${brand}'
brand='${brand}' and fn='${brand}'
brand='${brand}' or fn='${brand}'
) a group by a.device,brand
)a


